import React, {Component} from 'react';
import logo from '../logo.png';
import './App.css';
import Web3 from "web3";
import web3 from "../ethereum/web3";
import contratoToken from "../abis/Main.json"

class App extends Component {

    async componentWillMount() {
        // Carga de Web3
        await this.loadWeb3()
        // Carga de los datos de la blockchain
        await this.loadBlockchainData()
    }

    // Carga de Web3

    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        } else if (window.web3) {
            console.log('window.web3 esta activo')
            window.web3 = new Web3(web3.currentProvider)
        } else {
            window.alert(' "No ethereum browser is installed. Try it installing MetaMask "')
        }
    }

    // Carga de los datos de la blockchain
    async loadBlockchainData() {
        const web3 = window.web3
        // Carga de la cuenta
        const accounts = await web3.eth.getAccounts()
        this.setState({account: accounts[0]})
        console.log('account: ', this.state.account)
        const networkId = '5777'
        console.log('networkId: ', networkId)
        const networkData = contratoToken.networks[networkId]
        console.log('networkData: ', networkData)

        if (networkData) {
            const abi = contratoToken.abi
            const address = networkData.address
            console.log('abi', abi)
            console.log('address', address)
            const contract = new web3.eth.Contract(abi, address)
            this.setState({contract})

            // Direccion del contrato
            const smartContract = await this.state.contract.methods.getContract().call()
            console.log("direccion smart contract: ", smartContract)
        } else {
            window.alert('El Smart Contract no se ha desplegado en la red blockchain!')
        }
    }

    constructor(props) {
        super(props);
        this.state = {
            account: '',
            contract: null,
            direccion_smart_contract: '',
            owner: '',
            direccion: '',
            cantidad: 0,
            loading: false,
            errorMessage: '',
            address_balance: ''
        }
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
                    <a
                        className="navbar-brand col-sm-3 col-md-2 mr-0"
                        href="https://frogames.es/rutas-de-aprendizaje"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        DApp
                    </a>
                </nav>
                <div className="container-fluid mt-5">
                    <div className="row">
                        <main role="main" className="col-lg-12 d-flex text-center">
                            <div className="content mr-auto ml-auto">
                                <a
                                    href="https://frogames.es/rutas-de-aprendizaje"
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    <img src={logo} className="App-logo" alt="logo"/>
                                </a>
                                <h1>DApp</h1>
                                <p>
                                    Edita <code>src/components/App.js</code> y guarda para recargar.
                                </p>
                                <a
                                    className="App-link"
                                    href="https://frogames.es/rutas-de-aprendizaje"
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    APRENDE BLOCKCHAIN <u><b>AHORA! </b></u>
                                </a>
                            </div>
                        </main>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
